package com.example.bookstore.domain.db;

import android.content.Context;

import androidx.annotation.NonNull;
import androidx.room.Database;
import androidx.room.Room;
import androidx.room.RoomDatabase;
import androidx.room.migration.Migration;
import androidx.sqlite.db.SupportSQLiteDatabase;

import com.example.bookstore.domain.model.BookItem;

import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

@Database(
        entities = {BookItem.class},
        version = 1,
        exportSchema = false)
public abstract class BookDatabase extends RoomDatabase {

    static final Migration MIGRATION_1_2 = new Migration(1, 2) {
        @Override
        public void migrate(SupportSQLiteDatabase database) {
            database.execSQL("ALTER TABLE book "
                    + " ADD COLUMN preview_image TEXT");
        }
    };

    static final Migration MIGRATION_2_3 = new Migration(2, 3) {
        @Override
        public void migrate(SupportSQLiteDatabase database) {
            database.execSQL("UPDATE book"
                    + " SET preview_image = " + "''"
                    + " WHERE id = 1");

            database.execSQL("UPDATE book"
                    + " SET preview_image = " + "''"
                    + " WHERE id = 2");
            database.execSQL("UPDATE book"
                    + " SET preview_image = " + "''"
                    + " WHERE id = 3");
        }
    };

    static final Migration MIGRATION_3_4 = new Migration(3, 4) {
        @Override
        public void migrate(SupportSQLiteDatabase database) {
            database.execSQL("ALTER TABLE book "
                    + " ADD COLUMN cover_image_url TEXT");

            database.execSQL("UPDATE book"
                    + " SET cover_image_url = " + "'https://bookclub.ua/images/db/goods/36415_54535.jpg'"
                    + " WHERE id = 1");

            database.execSQL("UPDATE book"
                    + " SET cover_image_url = " + "'https://book-ye.com.ua/upload/iblock/1f9/3e03b5b9_9064_11e6_80c0_000c29ae1566_290a5341_7a67_11ea_812e_000c29ae1566.jpg'"
                    + " WHERE id = 2");

            database.execSQL("UPDATE book"
                    + " SET cover_image_url = " + "'https://vivat-book.com.ua/upload/iblock/f8f/f8fd6d72ed61cd3faa92a4fed787569d.jpg'"
                    + " WHERE id = 3");
        }
    };

    public abstract BookDAO bookDao();

    private static volatile BookDatabase INSTANCE;
    private static final int NUMBER_OF_THREADS = 2;
    static final ExecutorService databaseWriteExecutor =
            Executors.newFixedThreadPool(NUMBER_OF_THREADS);

    static BookDatabase getDatabase(final Context context) {
        if (INSTANCE == null) {
            synchronized (BookDatabase.class) {
                if (INSTANCE == null) {
                    INSTANCE = Room.databaseBuilder(context.getApplicationContext(),
                                    BookDatabase.class, "book_reader")
                            //.addMigrations(MIGRATION_1_2, MIGRATION_2_3, MIGRATION_3_4)
                            .addCallback(sRoomDatabaseCallback)
                            .build();
                }
            }
        }

        return INSTANCE;
    }

    private static RoomDatabase.Callback sRoomDatabaseCallback = new RoomDatabase.Callback() {

        @Override
        public void onCreate(@NonNull SupportSQLiteDatabase db) {
            super.onCreate(db);

            databaseWriteExecutor.execute(() -> {
                BookDAO dao = INSTANCE.bookDao();
                dao.deleteAll();

                BookItem book1 = new BookItem("Крадійка книжок", "Маркус Зузак", "Болючі спогади своїх батьків-емігрантів про такі несумісні речі, як дитинство, війна й Голокост, сучасний австралійський письменник Маркус Зузак поклав в основу історії про маленьку дівчинку Лізель Мемінґер, яка в страшні роки Другої світової, навчившись читати за посібником гробаря, збагнула силу Слів і, втративши все інше, вижила - завдяки лише цій силі.");
                book1.setImageData("");
                book1.setImageUrl("https://bookclub.ua/images/db/goods/36415_54535.jpg");

                BookItem book2 = new BookItem("Список Шиндлера", "Томас Кініллі", "Його ім'я відоме усьому світу і вже давно стало називним. Оскар Шиндлер. Людина, яка довела, що можна здійснити справжнє диво — подарувати життя... Дати шанс на порятунок тоді, коли сподіватися вже нема на що…");
                book2.setImageData("");
                book2.setImageUrl("https://book-ye.com.ua/upload/iblock/1f9/3e03b5b9_9064_11e6_80c0_000c29ae1566_290a5341_7a67_11ea_812e_000c29ae1566.jpg");

                BookItem book3 = new BookItem("На Західному фронті без змін", "Еріх Марія Ремарк", "В основу роману покладено реальні спогади автора про Першу світову війну. Коли Ремарку виповнилося вісімнадцять, його мобілізували до війська, а через рік перекинули на Західний фронт. Головний герой роману — молодий солдат Пауль Боймер — так само потрапляє в пекло війни. Те, що він бачить, не схоже на героїчні історії й оповідки. Реалії війни — це кров і бруд, крики і смерть, ницість і мужність, виживання на межі людських можливостей. По завершенню війни Пауль та його фронтові товариші не вписуються в життя мирних міст. Вони — «втрачене покоління», змужнілі підлітки, яких назавжди змінила війна…");
                book3.setImageData("");
                book3.setImageUrl("https://vivat-book.com.ua/upload/iblock/f8f/f8fd6d72ed61cd3faa92a4fed787569d.jpg");

                dao.insertBooks(book1, book2, book3);
            });
        }
    };

}
